mapper.select:
select * from
( select rownum as rn, b.*
   from (
    select * from board

      where LOWER(${category}) like '%'||LOWER(#{query})||'%'

    order by idx desc
  ) b where rownum < #{endIdx}
) where rn >= #{startIdx}

mapper.update:
UPDATE board SET
replyCount = replyCount + 1
WHERE idx = #{idx}
